package session_9thOctober2022;

public class Increment_Decrement_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//++ (increment operator)
		//-- (decrement operator)
		 
		//post increment, pre increment
		//post decrement, Pre decrement
		
		//ex
		
		/*
		 * int i = 1;
		 *  i++; // it is a post increment operator 
		 *  ++i; //it is a pre increment operator 
		 *  i--; // it is post decrement operator
		 *   --i; // it is pre decrement operator
		 */
		
		//Ctrl + shift + / this to comment multiple lines of code
		//Ctrl + shift +\ this is to uncomment multiple lines of code
		
		
		
		
		
		
		int i = 1;
		System.out.println(i);//1
		i++;
		System.out.println(i);//2
		++i;
		System.out.println(i);//3
		
		i--;
		System.out.println(i);//2
		--i;
		System.out.println(i);//1
		
	}

}
